(define (eval exp env) (cond ((self-evaluating? exp) exp) ((variable? exp) (lookup-variable-value exp env)) ((quoted? exp) (text-of-quotation exp)) ((assignment? exp) (eval-assignment exp env)) ((definition? exp) (eval-definition exp env)) ((if? exp) (eval-if exp env)) ((lambda? exp) (make-procedure (lambda-parameters exp) (lambda-body exp) env)) ((begin? exp) (eval-sequence (begin-actions exp) env)) ((cond? exp) (eval (cond->if exp) env)) ((application? exp) (apply (eval (operator exp) env) (list-of-values (operands exp) env))) (else (error "Unknown expression type - - EVAL" exp))))
function evaluate(stmt, env) { return is_self_evaluating(stmt) ? stmt : is_name(stmt) ? lookup_name_value(name_of_name(stmt), env) : is_constant_declaration(stmt) ? eval_constant_declaration(stmt, env) : is_variable_declaration(stmt) ? eval_variable_declaration(stmt, env) : is_assignment(stmt) ? eval_assignment(stmt, env) : is_conditional_expression(stmt) ? eval_conditional_expression(stmt, env) : is_function_definition(stmt) ? eval_function_definition(stmt, env) : is_sequence(stmt) ? eval_sequence(sequence_statements(stmt), env) : is_block(stmt) ? eval_block(stmt, env) : is_return_statement(stmt) ? eval_return_statement(stmt, env) : is_application(stmt) ? apply(evaluate(operator(stmt), env), list_of_values(operands(stmt), env)) : error(stmt, "Unknown statement type in evaluate: "); }
