(define (unify-match p1 p2 frame) (cond ((eq? frame 'failed) 'failed) ((equal? p1 p2) frame) ((var? p1) (extend-if-possible p1 p2 frame)) ((var? p2) (extend-if-possible p2 p1 frame)) ; *** ((and (pair? p1) (pair? p2)) (unify-match (cdr p1) (cdr p2) (unify-match (car p1) (car p2) frame))) (else 'failed)))
function unify_match(p1, p2, frame) { return frame === "failed" ? "failed" : equal(p1, p2) ? frame : is_var(p1) ? extend_if_possible(p1, p2, frame) : is_var(p2) ? extend_if_possible(p2, p1, frame) // *** : pair(p1) && pair(p2) ? unify_match(tail(p1), tail(p2), unify_match(head(p1), head(p2), frame)) : "failed"; }

    Depends-on?The function depends_on
    is a predicate that tests whether an expression
    proposed to be the value of a pattern variable depends on the variable.
    This must be done relative to the current frame because the expression
    may contain occurrences of a variable that already has a value that
    depends on our test variable.  The structure of
    depends-on?depends_on
    is a simple recursive tree walk in which we substitute for the values of
    variables whenever necessary.



        (define (depends-on? exp var frame)
        (define (tree-walk e)
        (cond ((var? e)
              (if (equal? var e)
              true
              (let ((b (binding-in-frame e frame)))
              (if b
              (tree-walk (binding-value b))
              false))))
              ((pair? e)
              (or (tree-walk (car e))
              (tree-walk (cdr e))))
              (else false)))
        (tree-walk exp))


function depends_on(exp, variable, frame) {
    function tree_walk(e) {
        if (is_var(e)) {
            if (equal(variable, e)) {
                return true;
            } else {
                const b = binding_in_frame(e, frame);
                if (b !== undefined) {
                    return tree_walk(binding_value(b));
                } else {
                    return false;
                }
            }
	} else if (is_pair(e)) {
            return tree_walk(head(e)) ||
                   tree_walk(tail(e));
        } else {
            return false;
        }
    }
    return tree_walk(exp);
}


